const deg=6,hr=document.querySelector("#hr"),mn=document.querySelector("#mn"),sc=document.querySelector("#sc");setInterval(()=>{let e=new Date,t=30*e.getHours(),r=6*e.getMinutes(),o=6*e.getSeconds();hr.style.transform=`rotateZ(${t+r/12}deg)`,mn.style.transform=`rotateZ(${r}deg)`,sc.style.transform=`rotateZ(${o}deg)`});